mapper.select:
select userid,username,password,role
from userlogin
where userid = #{userid} and password = #{password}

select userid,username,password,role
from userlogin
where userid = #{userid}

mapper.update:
update userlogin
set password = #{password}
where userid = #{userid}

update userlogin
set password = 'a123456'
where userid = #{userid}
